    backgroundPosition: 'center',
    width: '400px',
    height: '370px',
    border: '2px dotted salmon',
    margin: '100px',
    cursor: 'pointer',
  }

}));
const fileReader = new FileReader();
export function UploadImage({handleUploadImage}) {
  const classes = useStyles();
  const [origImage, setOrigImage] = useState('');
  const [newImage, setNewImage] = useState('');

  const [imageWidth, setImageWidth] = useState({width: 400});
  const [imageHeight, setImageHeight] = useState({height: 400});

  const setNewImageSize = async (img) => {
    setImageHeight({width: 400});
    setImageWidth({height: 400});
    if(img.width > img.height) {      
      if(imageWidth < img.width) {
        console.log('imagewidth ',imageWidth, '< img.width ',img.width)
        console.log('newHeight ',Math.floor(img.height*(400/img.width)))
        setImageWidth({width: 400});
        setImageHeight({height: Math.floor(img.height*(400/img.width))});
      } else {
        setImageWidth({width: img.width});
        setImageHeight({height: img.height});
      }      
    } else {
      if(imageHeight < img.height) {
        console.log('imageHeight ',imageHeight, '< img.heigth ',img.height)
        setImageWidth({width: Math.floor(img.width*(400/img.height))});
        setImageHeight({height: 400});        
      } else {
        setImageWidth({width: img.width});
        setImageHeight({height: img.height});
      } 
    }
    console.log(img.width, img.height, imageWidth, imageHeight)
    return;
  }

  fileReader.onload = function (event) {
    console.log('fileReader.onload')

    var image = new Image();
    image.onload = async function(){
        await setNewImageSize(image);
        var canvas=document.createElement("canvas");
        var context=canvas.getContext("2d");
        canvas.width=imageWidth.width;
        canvas.height=imageHeight.height;
        context.drawImage(image,
            0,
            0,
            image.width,
            image.height,
            0,
            0,
            canvas.width,
            canvas.height
        );
        // console.log('image.onload canvas.toDataURL()',canvas.toDataURL())
        setNewImage(canvas.toDataURL());
    }
    image.src=event.target.result;
  };

  useEffect(() => {
    // console.log('useEffect',origImage)
    const filterType = new RegExp('^(?:image//bmp|image//cis-cod|image/gif|image/ief|image/jpeg|image/jpeg|image/jpeg|image/pipeg|image/png|image/svg+xml|image/tiff|image/x-cmu-raster|image/x-cmx|image/x-icon|image/x-portable-anymap|image/x-portable-bitmap|image/x-portable-graymap|image/x-portable-pixmap|image/x-rgb|image/x-xbitmap|image/x-xpixmap|image/x-xwindowdump)$','i');
    const uploadImage = document.getElementById("upload-Image");

    //check and retuns the length of uploded file.
    if (uploadImage.files.length === 0) { 
      return; 
    }

    //Is Used for validate a valid file.
    const uploadFile = document.getElementById("upload-Image").files[0];
    if (!filterType.test(uploadFile.type)) {
      alert("Please select a valid image."); 
      return;
    }

    fileReader.readAsDataURL(uploadFile);
    // eslint-disable-next-line
  }, [fileReader, origImage])
  // var loadImageFile = function () {
  //   var uploadImage = document.getElementById("upload-Image");

  //   //check and retuns the length of uploded file.
  //   if (uploadImage.files.length === 0) { 
  //     return; 
  //   }

  //   //Is Used for validate a valid file.
  //   var uploadFile = document.getElementById("upload-Image").files[0];
  //   if (!filterType.test(uploadFile.type)) {
  //     alert("Please select a valid image."); 
  //     return;
  //   }

  //   fileReader.readAsDataURL(uploadFile);
  // }
  return (
    <>
    {/* <body className={classes.photoDrop}> */}
      {/* <form name="uploadForm"> */}
        <table>
          <tbody>
            <tr>
              <td><input id="upload-Image" type="file" onChange={(e) => setOrigImage(e.target.value)}/></td>
            </tr>
            {/* <tr>
              <td>Origal Img - <img id="original-Img" src={origImage} alt='original limage'/></td>
            </tr> */}
            {newImage.length ?
              <tr>
                <td><img id="upload-Preview" src={newImage} alt='resized limage'/></td>
              </tr>
              :
              <tr></tr>
            }


          </tbody>
        </table>
      {/* </form> */}
    {/* </body> */}
    </>
  )
}
