  //     alert("Please select a valid image."); 
  //     return;
  //   }

  //   fileReader.readAsDataURL(uploadFile);
  // }
  return (
    <>
    {/* <body className={classes.photoDrop}> */}
      {/* <form name="uploadForm"> */}
        <table>
          <tbody className={classes.photoDrop}>
            <tr>
              <td><input id="upload-Image" type="file" onChange={(e) => setOrigImage(e.target.value)}/></td>
            </tr>
            {/* <tr>
              <td>Origal Img - <img id="original-Img" src={origImage} alt='original limage'/></td>
            </tr> */}
            {newImage.length ?
              <tr>
                <td><img id="upload-Preview" src={newImage} alt='resized limage'/></td>
              </tr>
              :
              <tr></tr>
            }


          </tbody>
        </table>
      {/* </form> */}
    {/* </body> */}
    </>
  )
}
