        Email: 
        <IconButton name="email" aria-label="edit">
          {email} &nbsp;&nbsp;
          <EditIcon className={classes.icon}/>
        </IconButton>      
      </div>
      <div className={classes.formElement} onClick={() => handleClick('city')}>
        City: 
        <IconButton name="city" aria-label="edit">
          {city} &nbsp;&nbsp;
