import React, { useContext, useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { useHistory } from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
import { 
  Button, 
  TextField,
  Checkbox,
  FormControlLabel,
  Box,
} from '@material-ui/core';
import { 
  addNewPost,
 } from '../dogfriendsPostsSlice';
// import { useFormInput } from '../hooks/useFormInput';
import { AuthContext } from '../context/AuthContext';
// import FormInputOutlined from './FormInputOutlined';
import PostPhoto from './PostPhoto';
// import NewPhotoForm from './NewPhotoForm';
import UploadPhoto from './UploadPhoto';

import { v4 as uuid } from 'uuid';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    width: '100%',
    border: '1px solid red',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    width: '100%',
    margin: '30px 0 20px 0',
  },
  buttons: {

  },
  input: {
    width: '100%',
    backgroundColor: '#ffffff',
    marginRight: '5px',
  },
  memeInput: {
    // display: formInputState,
  },
  imagePreview: {
    width: 'auto',
    alignItems: 'flex-start',
    justifyContent: 'center',
  },
  image: {
    width: '400px',
    height: '400px',
    borderRadius: '10px',
    padding: '10px',
  }
}));

const NewPostForm = ({data}) => {
  const classes = useStyles();
  const auth = useContext(AuthContext);
  const dispatch = useDispatch();
  const history = useHistory();
  // console.log('NewPostForm auth',auth)
  const [title, setTitle] = useState('');
  const [topText, setTopText] = useState('');
  const [bottomText, setBottomText] = useState('');
  const [body, setBody] = useState('');
  const [color, setColor] = useState('');
  const [titleValid, setTitleValid] = useState('');
  const [topTextValid, setTopTextValid] = useState('');
  const [bottomTextValid, setBottomTextValid] = useState('');
  const [bodyValid, setBodyValid] = useState('');
  // const [colorValid, setColorValid] = useState(true);

  const handleSubmit = e => {   
    e.preventDefault();
    // if(title.length && description.length && body.length) {
    //   const payload = {
    //     id: 'id',
    //     title: title, 
    //     description: 'description', 
    //     body: body,
    //     username: auth.authState.userInfo.username,
    //     token: auth.authState.token
    //   }
    //   dispatch(addNewPost(payload));    
    //   history.push('/');
    // } 
    const payload = {
      parent_id: uuid(),
      photo_id: uuid(),
      title: title, 
      // description: 'description', 
      body: uuid(),
      // username: auth.authState.userInfo.username,
      token: auth.authState.token
    }
    dispatch(addNewPost(payload));    
  }

  // const title = useFormInput('');  
  // const top = useFormInput('');
  // const bottom = useFormInput('');
  // const body = useFormInput('');

  const [url, setUrl] = useState('');
  const [formInputState, setFormInputState] = useState('none');
  const [photoShowState, setPhotoShowState] = useState('none');
  const [photo, setPhoto] = useState({
    url: '',
    top: '',
    bottom: '',
    textColor: '#000000'
  });
  const [textColor, setTextColor] = useState('#000000');  
  const handleTextColor = (e) => {
    e.preventDefault();
    setTextColor(e.target.value);
  }
  useEffect(() => {
    // console.log('useEffect photoShowState',photoShowState)
    // console.log('useEffect url', url)
    if(url !== '') {
      setPhotoShowState('flex');
      setPhoto({
        url: url,
        top: top.value,
        bottom: bottom.value,
        textcolor: textColor
      });
    } else {
      setPhotoShowState('none');
      setPhoto({
        url: '',
        top: '',
        bottom: '',
        textcolor: textColor
      });
    }
    // eslint-disable-next-line
  }, [url, top.value, bottom.value, textColor]);

  const handleCheckBox = () => {
    if(formInputState === 'none') setFormInputState('inline')
    else setFormInputState('none')
  }

  return (
    <div className={classes.root}>    
      <Box className={classes.imagePreview} style={{display: photoShowState}}>
        <PostPhoto photo={photo}/>

      </Box>
      <form className={classes.form} >   

        <TextField 
          label="Title" 
          variant='outlined' 
          value={title}
          inputRef={input => !title.length && input && input.focus()}
          error={titleValid.length ? true : false}
          helperText={titleValid.length ? titleValid : ''}
          onChange={e => setTitle(e.target.value)} />
        <FormControlLabel
          control={<Checkbox onChange={handleCheckBox} />}
          label="Add text to photo."/>
        <div style={{display: formInputState}}>
          {/* <NewPhotoForm url={url} handleSetUrl={handleSetUrl} /> */}
          <UploadPhoto token={auth.authState.token} setUrl={setUrl}/>
          Text Color&nbsp;  
          <input           
            type="color" 
            onChange={handleTextColor}
            defaultValue={textColor} />
          <TextField 
            label="Top Text" 
            variant='outlined' 
            value={topText}
            error={topTextValid.length ? true : false}
            helperText={topTextValid.length ? topTextValid : ''}
            onChange={e => setTopText(e.target.value)} />
          <TextField 
            label="Bottom Text" 
            variant='outlined' 
            value={bottomText}
            error={bottomTextValid.length ? true : false}
            helperText={bottomTextValid.length ? bottomTextValid : ''}
            onChange={e => setBottomText(e.target.value)} />
          {/* <TextField rows={1} label="Top Text" variant='outlined' {...top} />
          <TextField rows={1} label="Bottom Text" variant='outlined' {...bottom} /> */}
          {/* <FormInputOutlined label="Top Text" formInput={top} />
          <FormInputOutlined label="Bottom Text" formInput={bottom} /> */}
        </div>
        <TextField 
          rows={4}
          label="Body" 
          variant='outlined' 
          value={body}
          error={bodyValid.length ? true : false}
          helperText={bodyValid.length ? bodyValid : ''}
          onChange={e => setBody(e.target.value)} />
        {/* <TextField 
          multiline
          rows={4} 
          label="Body" 
          variant="outlined" 
          {...body} /> */}

        <div className={classes.buttons}>
          <Button variant="contained" color="primary" onClick={handleSubmit} >
            Save
          </Button>
          <Button onClick={() => history.push('/')} variant="contained" color="default" >
            Cancel
          </Button>
        </div>      
      </form>
    </div>
  );
}
const useFormInput = () => {
  const [value, setValue] = useState('');
  const handleChange = e => setValue(e.target.value);
  return {
    value,
    onChange: handleChange,
  }
}
export default NewPostForm;
