import * as Resizer from 'react-image-file-resizer'

const ResizeImage = async ({image, width, height}) => {
  const resizeFile = () => new Promise(resolve => {
    Resizer.imageFileResizer(image, width, height, 'JPEG', 100, 0,
    uri => {
      resolve(uri);
    },
    'base64'
    );
  });
  return resizeFile
}

export default ResizeImage;
