// import Resizer from 'react-image-file-resizer';

const resizeFile = (file, w, h) => new Promise(resolve => {
  // const fr = new FileReader();
  let img = new Image();
  img.src = file[0].data_url;
  console.log('img.width',img.width)
  let canvas=document.createElement("canvas");
  let context=canvas.getContext("2d");
  canvas.width=img.width/4;
  canvas.height=img.height/4;
  context.drawImage(img,
      0,
      0,
      img.width,
      img.height,
      0,
      0,
      canvas.width,
      canvas.height
  );
  // console.log('fr',fr)
  console.log('ResizeImage file',Buffer.from(file[0].data_url))
  console.log('ResizeImage img', img)
  console.log('ResizeImage canvas',canvas.toDataURL())
  return img;
  // Resizer.imageFileResizer([img], w, h, 'JPEG', 100, 0,
  // uri => {
  //   resolve(uri);
  // },
  // 'base64'
  // );
});

const ResizeImage = async (file, width, height) => {
  const res = await resizeFile(file, width, height);
  console.log('res',res)
  return res
}

export default ResizeImage
