import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import {
  getUserInfo
} from './api/DogfriendsApi';


export const getUserInfoData = createAsyncThunk(
  'getVotes',
  async (token, username) => {
    const response = await getUserInfo(token, username);
    return response.data;
  }
);

export const dogfriendsUserSlice = createSlice({
  name: 'userList',
  initialState: {
    userList: {
      status: 'idle',
      data: [],
      error: {}
    }
  },
  extraReducers: {
    // get user info
    [getUserInfo.pending]: (state, action) => {
      state.userList = {
        status: 'pending',
        data: {},
        error: {}
      };
    },
    [getUserInfo.fulfilled]: (state, action) => {
      state.userList = {
        status: 'fulfilled',
        data: action.payload,
        error: {}
      };
    },
    [getUserInfo.rejected]: (state, action) => {
      state.userList = {
        status: 'rejected',
        data: {},
        error: action.payload,
      };
    }, 
  }
});


export const selectUser = state => state.userList.userList;

export default dogfriendsUserSlice.reducer;
