import {useState} from 'react';
import { preSignupCheck } from '../api/DogfriendsApi'
function useFormInput(initialValue='text') {
  const [value, setValue] = useState("");
  const [type] = useState(initialValue);

  const handleChange = async (e) => {
    if(e.target.name === 'usr_name' && await preSignupCheck(e.target.value)) {
      console.log('useFormInput username in use', e.target.value)
    }
    setValue(e.target.value);
  }
  return {
    value, 
    type,
    onChange: handleChange,
  };
}

export {useFormInput};
