import React, { useState, useEffect, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import { makeStyles } from '@material-ui/core/styles';
import Tabs from '@material-ui/core/Tabs'
import Tab from '@material-ui/core/Tab'
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import MenuIcon from '@material-ui/icons/Menu';
import IconButton from '@material-ui/core/IconButton';
import { useTheme } from '@material-ui/core/styles';

import { AuthContext } from '../context/AuthContext';
import UserAvatar from './UserAvatar';
import { getUserInfoSlice } from '../dogfriendsUserSlice';
import { logout } from '../dogfriendsUserSlice';


import logo from '../assets/logo.png';

const useStyles = makeStyles(theme => ({
  toolbarMargin: {
    ...theme.mixins.toolbar,
    marginBottom: '4em',
    [theme.breakpoints.down('md')]: {
      marginBottom: '2.7em',
    }, 
    [theme.breakpoints.down('xs')]: {
      marginBottom: '1.6em',
    }, 
  },
  logo: {
    height: '8em', 
    [theme.breakpoints.down('md')]: {
      height: '7em',
    }, 
    [theme.breakpoints.down('xs')]: {
      height: '5.5em'
    },   
  },
  logoContainer: {
    padding: '0'
  },
  tabContainer: {
    marginLeft: 'auto'
  },
  tab: {
    ...theme.typography.tab,
    minWidth: 10,
    marginLeft: '25px'
  },
  button: {
    ...theme.typography.button,
    borderRadius: '50px',
    margin: '0 25px 0 50px',    
    height: '45px',
  },
  menu: {
    backgroundColor: theme.palette.primary.main,
    color: 'white',
    borderRadius: 0,
  },
  menuItem: {
    ...theme.typography.tab,
    opacity: 0.7,
    '&:hover': {
      opacity: 1
    }
  },
  drawerIconContainer: {
    marginLeft: 'auto',
    '&:hover': {
      backgroundColor: 'transparent',
    }
  },
  drawerIcon: {
    fontSize: '2em',
    borderRadius: '15px',
  }
}));


function ElevationScroll(props) {
  const { children } = props;

  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0
  });
}

export default function Header(props) {
  const classes = useStyles();
  const auth = useContext(AuthContext);
//   const dispatch = useDispatch();
  const location = useLocation();
  const theme = useTheme();
  const iOS = process.browser && /iPad|iPhone|iPod/.test(navigator.userAgent);

  const matches = useMediaQuery(theme.breakpoints.down('md'));

  const [value, setValue] = useState(0);  
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [anchorEl, setAnchorEl] = useState(null);
  const [openMenu, setOpenMenu] = useState(false);  
  const [openDrawer, setOpenDrawer] = useState(false);

  const handleOpen = (e) => {
    setAnchorEl(e.currentTarget);
    setOpenMenu(true);
  }
  const handleClose = (e) => {
    setAnchorEl(null);
    setOpenMenu(false);
  }
  const handleMenuItemClick = (e, i) => {
    setAnchorEl(null);
    setOpenMenu(false);
    setSelectedIndex(i);
  }
  const menuOptions = [
    {
      name: 'Services',
      link: '/services'
    },
    {
      name: 'Costom Software Development',
      link: '/customsoftware'
    },
    {
      name: 'Mobile App Development',
      link: '/mobileapps'
    },
    {
      name: 'Website Development',
      link: '/websites'
    },

  ];
  useEffect(() => {
    switch(location.pathname) {
      case '/':
        setValue(0);
        break;
      case '/estimate':
        setValue(5);
        break;
      case '/revolution':
        setValue(2);
        break;
      case '/about':
        setValue(3);
        break;
      case '/contact':
        setValue(4);
        break;
      case '/customsoftware':
        setValue(1);
        setSelectedIndex(1);
        break;
      case '/mobileapps':
        setValue(1);
        setSelectedIndex(2);
        break;
      case '/websites':
        setValue(1);
        setSelectedIndex(3);
        break;
      default:
        setValue(1);
        setSelectedIndex(0);
    }  
    console.log('Header useEffect value',value)  
  }, [value, location.pathname]);

  const tabs = (
    <>
    <Tabs 
      className={classes.tabContainer} 
      value={value}               
      indicatorColor='primary'>
      <Tab 
        className={classes.tab} 
        label='Home' 
        component={Link} 
        to='/' />
      {auth.authState.userInfo.username &&
        (<>
          <Tab 
            aria-owns={anchorEl ? 'simple-menu' : undefined}
            aria-haspopup={anchorEl ? 'true' : undefined}
            className={classes.tab} 
            label='New Post' 
            component={Link} 
            onMouseOver={handleOpen}
            to='/new' />
          <Tab 
            className={classes.tab} 
            label='Profile' 
            component={Link} 
            to='/profile' />
        </>)
      }      
      <Tab 
        className={classes.tab} 
        label='About Us' 
        component={Link} 
        to='about' />
      <Tab 
        className={classes.tab} 
        label='Contact Us' 
        component={Link} 
        to='contact' />
    </Tabs>
    <Button 
      className={classes.button} 
      variant='contained' 
      color='secondary' 
      component={Link} 
      to='/login' >
      Login
    </Button>
    <Menu 
      id='simple-menu' 
      anchorEl={anchorEl} 
      open={openMenu}               
      onClose={handleClose}
      classes={{paper: classes.menu}}
      MenuListProps={{onMouseLeave: handleClose}}
      elevation={0}
      >
      {menuOptions.map((option, i) => (
        <MenuItem 
          key={option}
          component={Link} 
          to={option.link} 
          classes={{root: classes.menuItem}} 
          onClick={(event) => {handleMenuItemClick(event, i); setValue(1)}} 
          selected={i === selectedIndex && value === 1}
          >
          {option.name}
        </MenuItem>
      ))}
    </Menu>
    </>
  );

  const drawer = (
    <>
      <SwipeableDrawer 
        disableBackdropTransition={!iOS} 
        disableDiscovery={iOS}
        open={openDrawer}
        onClose={() => setOpenDrawer(false)}
        onOpen={() => setOpenDrawer(true)} >
          Example Drawer
      </SwipeableDrawer>
      <IconButton 
        className={classes.drawerIconContainer}
        onClick={() => setOpenDrawer(!openDrawer)}
        disableRipple >
        <MenuIcon className={classes.drawerIcon} />
      </IconButton>
    </>
  );

  return (
    <>
      <ElevationScroll>
        <AppBar position='fixed'>
          <Toolbar disableGutters>
            <Button className={classes.logoContainer} component={Link} to='/' disableRipple>
              <img src={logo} className={classes.logo} alt='company logo'/>
            </Button>            
            {matches ? drawer : tabs}
          </Toolbar>
        </AppBar>
      </ElevationScroll>
      <div className={classes.toolbarMargin} />
    </>

  );
}

