import React, { useState, useEffect, useContext } from 'react';
import { 
  Link,
  useLocation 
} from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import {
  Grid
} from '@material-ui/core';

import { AuthContext } from '../context/AuthContext';

import footerAdornment from '../assets/logo.png';

const useStyles = makeStyles(theme => ({
  footer: {
    backgroundColor: theme.palette.common.brown,
    width: '100%',
    zIndex: 1302,
    position: 'relative',
  },
  adornment: {
    width: '25em',
    verticalAlign: 'bottom',
    [theme.breakpoints.down('md')]: {
      width: '21em',
    },
    [theme.breakpoints.down('xs')]: {
      width: '15em',
    }
  },
  mainContainer: {
    width: '100%',
    position: 'absolute',
    justifyContent: 'center',
  },
  link: {
    color: 'white',
    fontFamily: 'Arial',
    fontSize: '0.75rem',
    fontWeight: 'bold',
    margin: '0.5em',
    textDecoration: 'none',
    opacity: 0.7,
    '&:active': {
      opacity: 1,
    }

  },
  linkSelected: {
    opacity: 1,
  },
  gridItem: {
    margin: '3em',
  },

}));
export default function Footer() {
  const classes = useStyles();
  const location = useLocation();
  const auth = useContext(AuthContext);
  const username = auth.authState.userInfo.username;
  const [value, setValue] = useState(0); 

  const listItems = {
    '/': {name: 'Home', index: 0},
    '/about': {name: 'About Us', index: 1},
    '/contact': {name: 'Contact Us', index: 2},
    '/login': {name: 'Logout', index: 3},
    '/profile': {name: 'Profile', index: 4},
    '/new': {name: 'New', index: 5}    
  } 
  // : {
  //   '/': {name: 'Home', index: 0},
  //   '/about': {name: 'About Us', index:1},
  //   '/contact': {name: 'Contact Us', index: 2},
  //   '/login': {name: 'Login', index: 3},
  // };

  useEffect(() => {
    setValue(listItems[location.pathname].index);
    console.log(value)
  }, [listItems, location.pathname]);

  return (
    <footer className={classes.footer}>
      <Grid container className={classes.mainContainer}>
        <Grid item className={classes.gridItem}>
          <Grid container direction='column'>
            <Grid 
              className={classes.link}
              item 
              component={Link} 
              to='/' 
              selected={value === 0}
              >
              Home
            </Grid>
            <Grid item component={Link} to='/about' className={classes.link}>
              About Us
            </Grid>
            <Grid item component={Link} to='/contact' className={classes.link}>
              Contact Us
            </Grid>
          </Grid>
        </Grid>
        <Grid item className={classes.gridItem}>
          <Grid container direction='column'>
            <Grid item component={Link} to='/login' className={classes.link}>
              Login
            </Grid>
            <Grid item component={Link} to='/profile' className={classes.link}>
              Profile
            </Grid>
          </Grid>
        </Grid>
      </Grid>
      <img
        alt='black decorative slash'
        src={footerAdornment}
        className={classes.adornment}
      />
    </footer>
  )

}
