// import ResizeImage from './ResizeImage';

const useStyles = makeStyles((theme) => ({
  photoDrop: {
    backgroundImage: `url(${photo_drop_zone})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    width: '400px',
