}));
const fileReader = new FileReader();
export function UploadImage({handleUploadImage}) {
  const classes = useStyles();
  const [origImage, setOrigImage] = useState(
    document.getElementById('upload-Image')
  );
  const [newImage, setNewImage] = useState('');



  fileReader.onload = function (event) {
    console.log('fileReader.onload')
    var image = new Image();
    image.onload=function(){
        setOrigImage(image.src);
        var canvas=document.createElement("canvas");
        var context=canvas.getContext("2d");
        canvas.width=image.width/4;
        canvas.height=image.height/4;
        context.drawImage(image,
            0,
            0,
            image.width,
            image.height,
            0,
            0,
            canvas.width,
            canvas.height
        );
        console.log('image.onload canvas.toDataURL()',canvas.toDataURL())
        setNewImage(canvas.toDataURL());
    }
    image.src=event.target.result;
  };

  useEffect(() => {
    console.log('useEffect',origImage)
    const filterType = new RegExp('^(?:image//bmp|image//cis-cod|image/gif|image/ief|image/jpeg|image/jpeg|image/jpeg|image/pipeg|image/png|image/svg+xml|image/tiff|image/x-cmu-raster|image/x-cmx|image/x-icon|image/x-portable-anymap|image/x-portable-bitmap|image/x-portable-graymap|image/x-portable-pixmap|image/x-rgb|image/x-xbitmap|image/x-xpixmap|image/x-xwindowdump)$','i');
    const uploadImage = document.getElementById("upload-Image");

    //check and retuns the length of uploded file.
    if (uploadImage.files.length === 0) { 
      return; 
    }

    //Is Used for validate a valid file.
    const uploadFile = document.getElementById("upload-Image").files[0];
    if (!filterType.test(uploadFile.type)) {
      alert("Please select a valid image."); 
      return;
    }

    fileReader.readAsDataURL(uploadFile);
    // eslint-disable-next-line
  }, [fileReader, origImage])
  // var loadImageFile = function () {
  //   var uploadImage = document.getElementById("upload-Image");

