  //     alert("Please select a valid image."); 
  //     return;
  //   }

  //   fileReader.readAsDataURL(uploadFile);
  // }
  return (
    <>
    {/* <body className={classes.photoDrop}> */}
      {/* <form name="uploadForm"> */}
        <table>
          <tbody>
            <tr>
              <td className={classes.photoDrop}><input id="upload-Image" type="file" onChange={(e) => setOrigImage(e.target.value)}/></td>
            </tr>
            <tr>
              <td>Origal Img - <img id="original-Img" src={origImage} alt='original limage'/></td>
            </tr>
            <tr>
              <td>Compress Img - <img id="upload-Preview" src={newImage} alt='resized limage'/></td>
            </tr>

          </tbody>
        </table>
      {/* </form> */}
    {/* </body> */}
    </>
  )
}
