  // }, [dispatch]);

  console.log('Profile userList',userList)
  const history = useHistory();  

  const handleClick = e => {
    console.log(e.target)
  }

  return (
    <div className={classes.root}>
    <h1>Your Profile</h1>
    <div className={classes.form}>
      <div className={classes.formElement} onClick={handleClick}><UserAvatar /></div>
      <div className={classes.formElement}>Username: {username}</div>
      <div className={classes.formElement}>First: {first_name}</div>
      <div className={classes.formElement}>Last: {last_name}</div>
      <div className={classes.formElement}>Email: {email}</div>
      <div className={classes.formElement}>City: {city}</div>
      <div className={classes.formElement}>State: {state}</div>
      <div className={classes.formElement}>Country: {country}</div>      
    </div>
    </div>
  );
}
export default Profile;
