/** Login and Signup */
import React, { useContext, useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Box, Button, TextField } from '@material-ui/core';
import { useFormInput } from '../hooks/useFormInput';
import { AuthContext } from '../context/AuthContext';
// import FormInputOutlined from './FormInputOutlined';
import { loginSlice, getUserInfoSlice } from '../dogfriendsUserSlice';
import { useHistory } from 'react-router-dom';
import { useDispatch } from 'react-redux';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    width: '100%',
    maxWidth: '700px', 
    paddingTop: '20px',
    border: '1px solid red'

  },
  formElement: {
    width: '95%',
    marginTop: '20px',
    marginBottom: '20px',
  },
  button: {
    display: 'flex',
    border: '1px solid red',
  },
  err: {
    color: '#ff1744',
    fontSize: '24px',
  },

  completed: {
    display: 'inline-block',
  },

}));


export default function LoginForm() {
  const classes = useStyles();
  const history = useHistory();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const auth = useContext(AuthContext);
  const dispatch = useDispatch();
  const [errorMessage, setErrorMessage] = useState(false);
  // const [usernameValue, setUsernameValue] = useState('username'); 
  // const [formState, setFormState] = useState("userInfo"); 

  // const handleChange = (e, newValue) => {
  //   setValue(newValue);
  // }
  useEffect(() => {
    setErrorMessage(false);
  }, [username.value, password.value])

  const handleSubmitLogin = async (e) => {
    e.preventDefault();
    console.log('LoginForm handleSubmitForm')
    setErrorMessage(false);
    try {
      // verify username and password are correct
      // const resp = await login(username.value, password.value);
      const resp = await dispatch(loginSlice({
        username: username.value,
        password: password.value
      }));
      console.log('LoginForm handleSubmitForm resp',resp)
      // if logged in, use resp.token to get user information
      const userInfo = await dispatch(getUserInfoSlice({
        token: resp.payload.token, 
        username: username.value
      }));

      // console.log('LoginForm handleSubmitLogin userInfo',userInfo)

      auth.setAuthState({
        userInfo: userInfo.payload.user,
        token: resp.payload.token,
      });
      history.push(`/`);
    } catch (error) {
      setErrorMessage(true);
      console.log(error)
    }     
  }

  return (
    <form className={classes.root} onSubmit={handleSubmitLogin}>
      {/* {console.log(errorMessage)} */}
       <Box className={classes.err} 
        component="span" 
        display={errorMessage ? 'block' : 'none'}
        >
          Error: Invalid credentials
      </Box>
      <TextField 
        className={classes.formElement} 
        label='Username: (required)' 
        variant='outlined' 
        value={username} 
        onChange={e => setUsername(e.target.value)}/>
      <TextField 
        className={classes.formElement} 
        label='Password: (required)' 
        variant='outlined' 
        value={password} 
        onChange={e => setPassword(e.target.value)}/>
      <div className={classes.button}>
        <Button type="submit" variant="contained" color="primary" >Submit</Button>
      </div>
    </form>
  );
}
