import * as AWS from 'aws-sdk';

import { v4 as uuid } from 'uuid';

const BUCKET_NAME = process.env.REACT_APP_BUCKET_NAME;

const s3 = new AWS.S3({
  region: process.env.REACT_APP_AWS_DEFAULT_REGION,
  secretAccessKey: process.env.REACT_APP_AWS_SECRET_ACCESS_KEY,
  accessKeyId: process.env.REACT_APP_AWS_ACCESS_KEY_ID,
});

const uploadToS3 = async (data) => {
  console.log('DogfriendsPhotoApi uploadToS3 BUCKET_NAME',BUCKET_NAME)
  let mime = data.name.slice(data.name.lastIndexOf('.'), data.name.length);
  const name = uuid() + '.' + mime;
  await s3.putObject({
    Key: name,
    Bucket: BUCKET_NAME,
    ContentType: `image/${mime}`,
    Body: data,
    ACL: 'public-read',
  }).promise(); 
  return name;
}

const postNewPhoto = async (data) => {
  try {
    const url = await uploadToS3(data);
    console.log('url',url)
    return url;
  } catch (error) {
    console.log(error);
  }
}


export {
  postNewPhoto,
}
