import React from 'react';
// import ImageUploading from 'react-images-uploading';
import photo_drop_zone from '../assets/photo_drop_zone.jpg';
// import Container from '@material-ui/core/Container';
import { makeStyles } from '@material-ui/core/styles';

// import ResizeImage from './ResizeImage';

const useStyles = makeStyles((theme) => ({
  photoDrop: {
    backgroundImage: `url(${photo_drop_zone})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    width: '400px',
    height: '370px',
    border: '2px dotted salmon',
    margin: '100px',
    cursor: 'pointer',
  }

}));
export function UploadImage({handleUploadImage}) {
  const classes = useStyles();
  const fileReader = new FileReader();
  const filterType = new RegExp('^(?:image//bmp|image//cis-cod|image/gif|image/ief|image/jpeg|image/jpeg|image/jpeg|image/pipeg|image/png|image/svg+xml|image/tiff|image/x-cmu-raster|image/x-cmx|image/x-icon|image/x-portable-anymap|image/x-portable-bitmap|image/x-portable-graymap|image/x-portable-pixmap|image/x-rgb|image/x-xbitmap|image/x-xpixmap|image/x-xwindowdump)$','i');

  fileReader.onload = function (event) {
    var image = new Image();

    image.onload=function(){
        document.getElementById("original-Img").src=image.src;
        var canvas=document.createElement("canvas");
        var context=canvas.getContext("2d");
        canvas.width=image.width/4;
        canvas.height=image.height/4;
        context.drawImage(image,
            0,
            0,
            image.width,
            image.height,
            0,
            0,
            canvas.width,
            canvas.height
        );

        document.getElementById("upload-Preview").src = canvas.toDataURL();
    }
    image.src=event.target.result;
  };

  var loadImageFile = function () {
    var uploadImage = document.getElementById("upload-Image");

    //check and retuns the length of uploded file.
    if (uploadImage.files.length === 0) { 
      return; 
    }

    //Is Used for validate a valid file.
    var uploadFile = document.getElementById("upload-Image").files[0];
    if (!filterType.test(uploadFile.type)) {
      alert("Please select a valid image."); 
      return;
    }

    fileReader.readAsDataURL(uploadFile);
  }
  return (
    <>
    <body className={classes.photoDrop} onload={loadImageFile}>
      <form name="uploadForm">
        <table>
          <tbody>
            <tr>
              <td>Select Image - <input id="upload-Image" type="file" onchange={loadImageFile} /></td>
            </tr>
            <tr>
              <td>Origal Img - <img id="original-Img"/></td>
            </tr>
            <tr>
              <td>Compress Img - <img id="upload-Preview"/></td>
            </tr>

          </tbody>
        </table>
      </form>
    </body>
    </>
  )
}
