import React, { useContext, useEffect } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Navbar from './features/dogfriends/components/Navbar'; 
import Home from './features/dogfriends/pages/Home';
import NewPost from './features/dogfriends/pages/NewPost';
import Login from './features/dogfriends/pages/Login';
import PostList from './features/dogfriends/pages/PostList';
import { makeStyles } from '@material-ui/core';
import Profile from './features/dogfriends/pages/Profile';

import {useSelector, useDispatch} from 'react-redux';
import { selectUser } from '../src/features/dogfriends/dogfriendsUserSlice';
import { AuthContext } from '../src/features/dogfriends/context/AuthContext';
import { getUserInfo } from '../src/features/dogfriends/api/DogfriendsApi';
import { setUserList } from '../src/features/dogfriends/dogfriendsUserSlice';


const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: '20px',
    width: '100%',
  },
  main: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    [theme.breakpoints.down('md')]: {
      width: '100%'
    },
    [theme.breakpoints.up('lg')]: {
      width: '85%'
    },
    [theme.breakpoints.up('xl')]: {
      width: '75%'
    },
    border: 'none',
  },
}));
function App() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const userList = useSelector(selectUser);
  console.log('App userList', userList)
  const auth = useContext(AuthContext);
  const token = auth.authState.token;
  const username = auth.authState.userInfo.username;
  console.log('App auth',auth)
  console.log('App token',token)
  console.log('App username',username)
  const getUserInfoData = async () => {
    let res = await getUserInfo({username, token});
    console.log('App res',res)
    await dispatch(setUserList(res.data.user));
    if(userList.photo_id && !userList.photo_ur) {
      console.log('App userList.photo_id && !userList.photo_ur', userList.photo_id , !userList.photo_ur)
    }
  }
  useEffect(() => {
    getUserInfoData();
    // eslint-disable-next-line
  }, [token, username]);
  

  return (
    <BrowserRouter>
        <Navbar/>
          <div className={classes.root}>
            <main className={classes.main}>
            <Switch>        
              <Route exact path="/">
                <Home />        
              </Route>
              <Route exact path="/login">
                <Login/>        
              </Route>
              <Route exact path="/new">
                <NewPost/>        
              </Route>
              <Route exact path="/profile">
                <Profile/>        
              </Route>
              <Route exact path="/:id">
                <PostList/>        
              </Route>                 
                               
            </Switch> 
            </main>        
          </div>        
    </BrowserRouter>
  );
}

export default App;
