/** Login and Signup */
import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
// import AppBar from '@material-ui/core/AppBar';
// import Tabs from '@material-ui/core/Tabs';
// import Tab from '@material-ui/core/Tab';
import { Container, FormHelperText, Box, Button } from '@material-ui/core';
import OutlinedInput from '@material-ui/core/OutlinedInput';
// import { login, signup, getUserInfo } from '../api/DogfriendsApi';
import { useFormInput } from '../hooks/useFormInput';


import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
// import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import FormInputOutlined from './FormInputOutlined';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'flex-start',
    width: '100%',
    minHeight: '100vh',
    maxWidth: '700px',
    padding: '2px',
    height: '100vh',
    // border: '1px solid black',
  },
  form: {
    width: '100%',
    backgroundColor: '#ffffff',
    // border: '1px solid red',
  },
  label: {
    fontSize: '18px',
    fontWeight: 'bold',
    width: '100%',
    marginTop: '10px',
    // margin: '5px 0 0 0',
  },
  input: {
    width: '100%',
    // margin: '0 20px 0 0',
  },
  button: {
    display: 'flex',
    justifyContent: 'flex-end',
    margin: '10px 20px 15px',
  },
  err: {
    color: '#ff1744',
    fontSize: '24px',
  },
  backButton: {
    marginRight: theme.spacing(1),
  },
  completed: {
    display: 'inline-block',
  },
  instructions: {
    // marginTop: theme.spacing(1),
    // marginBottom: theme.spacing(1),
  },

}));
function getSteps() {
  return ['User Info', 'Contact', 'Confirm'];
}

// function getStepContent(step) {
//   switch (step) {
//     case 0:
//       return 'Step 1: Select campaign settings...';
//     case 1:
//       return 'Step 2: What is an ad group anyways?';
//     case 2:
//       return 'Step 3: This is the bit I really care about!';
//     default:
//       return 'Unknown step';
//   }
// }

export default function SignupForm() {
  const classes = useStyles();
  const username = useFormInput("");
  const password = useFormInput("");
  const first_name = useFormInput("");
  const last_name = useFormInput("");  
  const photo_url = useFormInput("");
  const email = useFormInput("");
  const city = useFormInput("");
  const state = useFormInput("");
  const country = useFormInput("");
  const [errorMessage, setErrorMessage] = useState(false); 
  // const [formState, setFormState] = useState("userInfo"); 

  // const handleChange = (e, newValue) => {
  //   setValue(newValue);
  // }

  const handleSubmitSignup = async () => {
    setErrorMessage(false);
    try {
      const resp = await signup(
          username.value, 
          password.value, 
          first_name.value, 
          last_name.value, 
          photo_url.value, 
          email.value);
      auth.setAuthState({
        token: resp.data.token,
        userInfo: {
          username: username.value,
          first_name: first_name.value,
          last_name: last_name.value,
          photo_url: photo_url.value,
          email: email.value,
        }
      });
      history.push(`/`);
    } catch (error) {
      setErrorMessage(true);
    }    
  }
  ////////////////////////////////////////////////////////////
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = () => {
    if(activeStep === 0) {
      if(username.value.length && password.value.length && first_name.value.length && last_name.value.length) {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
      }
    } else if(activeStep === 1) {
      if(email.value.length && city.value.length && state.value.length && country.value.length) {
        setActiveStep((prevActiveStep) => prevActiveStep + 1);
      }
    } else {

    }

  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };
  ////////////////////////////////////////////////////////////
  return (
    <>
    <div className={classes.root}>
      <Stepper activeStep={activeStep}>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      {activeStep === 0 ?
      (<form className={classes.form}>      
        {/* <FormHelperText className={classes.label} >username</FormHelperText>
        <OutlinedInput className={classes.input} name="username" variant="outlined" {...username}/> */}
        <FormInputOutlined label={'username'} formInput={username} />

        <FormHelperText className={classes.label} >Password</FormHelperText>
        <OutlinedInput className={classes.input} name="password" type="password" variant="outlined" {...password}/>

        <FormHelperText className={classes.label} >First Name</FormHelperText>
        <OutlinedInput className={classes.input} name="first_name" variant="outlined" {...first_name}/>

        <FormHelperText className={classes.label} >Last Name</FormHelperText>
        <OutlinedInput className={classes.input} name="last_name" variant="outlined" {...last_name}/>

        <FormHelperText className={classes.label} >Photo URL (optional)</FormHelperText>
        <OutlinedInput className={classes.input} name="photo_url" variant="outlined" {...photo_url}/>

      </form>) :
      activeStep === 1 ?
      (<form className={classes.form}> 
        <FormHelperText className={classes.label} >Email</FormHelperText>
        <OutlinedInput className={classes.input} name="email" variant="outlined" {...email}/> 

        <FormHelperText className={classes.label} >City</FormHelperText>
        <OutlinedInput className={classes.input} name="city" variant="outlined" {...city}/>

        <FormHelperText className={classes.label} >State</FormHelperText>
        <OutlinedInput className={classes.input} name="state" variant="outlined" {...state}/>

        <FormHelperText className={classes.label} >Country</FormHelperText>
        <OutlinedInput className={classes.input} name="country" variant="outlined" {...country}/>        

      </form>) :
      ( 
        <>
        <div>{username.value}</div>
        <div>{first_name.value}</div>
        <div>{last_name.value}</div>
        <div>{email.value}</div>
        <div>{city.value}</div>
        <div>{state.value}</div>
        <div>{country.value}</div>
        </>
      )
    }


      <div>
        {activeStep === steps.length ? (
          <div>
            <Typography className={classes.instructions}>All steps completed</Typography>
            <Button onClick={handleReset}>Reset</Button>
          </div>
        ) : (
          <div>
            {/* <Typography className={classes.instructions}>{getStepContent(activeStep)}</Typography> */}
            <div>
              <Button
                disabled={activeStep === 0}
                onClick={handleBack}
                className={classes.backButton}
              >
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={handleNext}>
                {activeStep === steps.length - 1 ? 'Submit' : 'Next'}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>

   </>
  );
}




/**
 * "username": "Henry", 
	"password": "henry12345", 
	"first_name": "Henry", 
	"last_name": "Henry", 
	"email": "henry@henry.com",
	"photo_url": "https://images.unsplash.com/photo-1501196354995-cbb51c65aaea?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60", 
	"admin": false,
	"city": "Olympia",
	"state": "WA",
	"country": "USA"
 */
