import * as AWS from 'aws-sdk';

import { v4 as uuid } from 'uuid';

const BUCKET_NAME = 'dogfriends';

const s3 = new AWS.S3({
  region: 'us-west-2',
  secretAccessKey: 'L2jl2AOgtKyJw1EN9xW/6gvOpR2ZTVzOima3afOo',
  accessKeyId: 'AKIAUCUWDY6W57HR5TNU',
});

const uploadToS3 = async (data, mime) => {
  console.log('DogfriendsPhotoApi uploadToS3 BUCKET_NAME',BUCKET_NAME)
  console.log('DogfriendsPhotoApi uploadToS3 data',data)
  // let mime = data.name.slice(data.name.lastIndexOf('.'), data.name.length);
  const name = uuid() + mime;

  await s3.putObject({
    Key: name,
    Bucket: BUCKET_NAME,
    ContentType: `image/${mime}`,
    Body: data,
    ACL: 'public-read',
  }).promise(); 
  return name;
}

const postNewPhoto = async (data) => {
  try {
    const url = await uploadToS3(data);
    console.log('url',url)
    return url;
  } catch (error) {
    console.log(error);
  }
}


export {
  postNewPhoto,
}
