import {useState} from 'react';
import { preSignupCheck } from '../api/DogfriendsApi'
function useFormInput(initialValue='text') {
  const [value, setValue] = useState("");
  const [type] = useState(initialValue);

  const handleChange = async (e) => {
    const isInUse = await preSignupCheck(e.target.value);
    console.log('isInUse ',isInUse)
    if(e.target.name && e.target.name === 'usr_name' && isInUse) {
      console.log('useFormInput username in use', e.target.value)
    } else {
      console.log('useFormInput username not in use',e.target.name, e.target.value, isInUse)
    }
    setValue(e.target.value);
  }
  return {
    value, 
    type,
    onChange: handleChange,
  };
}

export {useFormInput};
