import * as Resizer from 'react-image-file-resizer'

const ResizeImage = async ({image, width, height}) => {
  const resizeFile = (file, width, height) => new Promise(resolve => {
    return Resizer.imageFileResizer(file, w, h, 'JPEG', 100, 0,
    uri => {
      resolve(uri);
    },
    'base64'
    );
  });
  return await resizeFile(image, width, height);
}

export default ResizeImage;
