import React , { useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { makeStyles } from '@material-ui/core';
// import {
//   postPhotoNew
// } from '../dogfriendsPhotosSlice';

import axios from 'axios';


const useStyles = makeStyles((theme) => ({
  dropzone: {
    height: '8rem',
    margin: '1rem',
    padding: '1rem',
    border: '2px dashed salmon',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    fontSize: '2rem',
    fontWeight: 'bold',
    cursor: 'pointer',
    active: {
      border: '2px solid rebeccapurple',
    },
  },

}));


// const BASE_URL = process.env.REACT_APP_CLOUDINARY_BASE_URL;

const request = async (endpoint, paramsOrData = {}, verb = 'get') => {

  console.log('API Call:', endpoint, paramsOrData, verb);
  try {
    const res = await axios({
      method: verb,
      url: endpoint,
      [verb === "get" ? "params" : "data"]: paramsOrData});

    return res;
      // axios sends query string data via the "params" key,
      // and request body data via the "data" key,
      // so the key we need depends on the HTTP verb
  }catch(err) {
    let message = err.response ? err.response.data.message : err;
    throw Array.isArray(message) ? message : [message];
  }
}
const postNewPhoto = async (acceptedFiles) => {
  console.log('postNewPhoto')
  const url = process.env.REACT_APP_CLOUDINARY_BASE_URL+'upload';
  const formData = new FormData();
  formData.append('file', acceptedFiles[0]);
  formData.append('upload_preset', process.env.REACT_APP_CLOUDINARY_UPLOAD_PRESET);

  const response = await request(url, formData, 'post');
  // const response = await fetch(url, {method: 'post', body: formData});
  const data = await response.json();   
  console.log('UploadPhoto postNewPhoto data',data)   
  return data;
  // acceptedFiles.forEach(async (acceptedFile) => {    

  //   // for dev we will just return this url so we don't keep making calls to Cloudinary API
  //   // return "http://res.cloudinary.com/dsxlpdoea/image/upload/v1605993605/photo-1544568100-847a948585b9_evaw8c.jpg";

  // });
}



export default function UploadPhoto() {
  const classes = useStyles();

  const onDrop = useCallback(async (acceptedFiles) => {
    console.log('UploadPhoto acceptedFiles',acceptedFiles)
    const response = await postNewPhoto(acceptedFiles);
    // const response = await postPhotoNew(acceptedFiles);
    // const data = await response.json();

    console.log('UploadPhoto response',response)   

  }, []);

  const {getRootProps, getInputProps} = useDropzone({
    onDrop, 
    accepts: 'image/*',
    multiple: false,
  });

  return (
    <>
      <div className={classes.dropzone} {...getRootProps()}>
        <input {...getInputProps()} />
        Drop Zone
      </div>
    </>
  );
}
