import React, { useContext } from 'react';

import { 
  Grid,
  Button,
  Typography,
  makeStyles,
 } from '@material-ui/core';

import ButtonArrow from '../components/ButtonArrow';
import CardImageList from '../components/CardImageList';
import { AuthContext } from '../context/AuthContext';

const useStyles = makeStyles((theme) => ({
  heading: {
    alignItems: 'center',
    justifyContent: 'center',
    // border: '1px solid red',
  },
  buttons: {
    alignItems: 'center',
    justifyContent: 'center',
    margin: '20px',
    // border: '1px solid blue',
  },
  cardImageList: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: '25px',
    // border: '1px solid red',
  }
}))
const Home = () => {
  const classes = useStyles();
  const auth = useContext(AuthContext);
  const username = auth.authState.userInfo.username;
  return (
    <Grid container direction='column'>
      <Grid sm item>
        <Grid container justify='center' alignItems='center' direction='row'>
          <Grid container className={classes.heading}>
            {username ? 
            <Typography variant='h2' align='center'>
              Welcome back {username}.
            </Typography> :
            <Typography variant='h2' align='center'>
              Share Your Dog Photos
              <br />
              with other dog friends
            </Typography>
            }
          </Grid>
          <Grid container className={classes.buttons}>
            <Grid item>
              <Button variant='contained'>
                Get Started
                <ButtonArrow width='15' height='15' fill='red' />
              </Button>
            </Grid>
            <Grid item>
              <Button variant='outlined'>Login</Button>
            </Grid>
          </Grid>

        </Grid>
      </Grid>
      <Grid sm item>
        <Grid container className={classes.cardImageList}>
          <CardImageList imageCount={10} />
        </Grid>        
      </Grid>
    </Grid>
  );
}

export default Home;


